public class Increments_Examples {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int value = 10;
		
		System.out.printf("\nThe initial value:\t%d",value);
		
		value++;
		System.out.printf("\nAfter value++    :\t%d",value);
		
		++value;
		System.out.printf("\nAfter ++value    :\t%d",value);
		
		value+=1;
		System.out.printf("\nAfter Value +=1  :\t%d",value);
		
		value--;
		System.out.printf("\nAfter value--    :\t%d",value);
		
		value-=1;
		System.out.printf("\nAfter Value -=1  :\t%d",value);
		
		
		System.out.println("\n=================================");
		System.out.printf("\nThe current value  : %d",value);
		
		
		System.out.printf("\nThe current value++: %d",value++);
		
		System.out.printf("\nThe current ++value++: %d",++value);
		//System.out.printf("\nThe current value++: %d",value++);
		//System.out.printf("\nThe current value++: %d",value++);


		

		


	}

}
